// Total exercise count
const TOTAL_EXERCISES: number = Object.values(EXERCISE_LIBRARY).reduce((sum: number, category: LibraryExercise[]) => sum + category.length, 0);

/**
 * Search exercises by name, muscle, or equipment
 */
function searchExercises(query: string | null, filters: SearchFilters = {}): LibraryExerciseWithCategory[] {
  if (!query && !filters.muscle && !filters.equipment && !filters.difficulty) {
    return getAllExercises();
  }

  const q = (query || '').toLowerCase();
  const results: LibraryExerciseWithCategory[] = [];

  for (const [category, exercises] of Object.entries(EXERCISE_LIBRARY)) {
    for (const exercise of exercises) {
      let matches = true;

      if (q && !exercise.name.toLowerCase().includes(q) && !exercise.id.includes(q)) matches = false;
      if (filters.muscle) {
        const allMuscles = [...(exercise.muscles.primary || []), ...(exercise.muscles.secondary || [])];
        if (!allMuscles.some(m => m.toLowerCase().includes(filters.muscle!.toLowerCase()))) matches = false;
      }
      if (filters.equipment && !exercise.equipment.some(e => e.toLowerCase().includes(filters.equipment!.toLowerCase()))) matches = false;
      if (filters.difficulty && exercise.difficulty !== filters.difficulty) matches = false;
      if (filters.type && exercise.type !== filters.type) matches = false;

      if (matches) results.push({ ...exercise, category });
    }
  }

  return results;
}

function getAllExercises(): LibraryExerciseWithCategory[] {
  const all: LibraryExerciseWithCategory[] = [];
  for (const [category, exercises] of Object.entries(EXERCISE_LIBRARY)) {
    for (const exercise of exercises) {
      all.push({ ...exercise, category });
    }
  }
  return all;
}

function getExercisesByMuscle(muscle: string): LibraryExerciseWithCategory[] {
  return searchExercises(null, { muscle });
}

function getExercisesByEquipment(equipment: string): LibraryExerciseWithCategory[] {
  return searchExercises(null, { equipment });
}

function getExerciseById(id: string): LibraryExerciseWithCategory | null {
  for (const [category, exercises] of Object.entries(EXERCISE_LIBRARY)) {
    const found = exercises.find(e => e.id === id);
    if (found) return { ...found, category };
  }
  return null;
}

